import React, { useState } from 'react';
import axios from 'axios';

const DassForm = () => {
    const [answers, setAnswers] = useState({});

    // Fetch the patient name from local storage
    const patientName = localStorage.getItem('patientName');

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Submit the answers and patient name
        try {
            const response = await axios.post('/submit_dass_report', {
                name: patientName,
                answers: answers,
            });

            if (response.status === 200) {
                alert('DASS report submitted successfully!');
            }
        } catch (error) {
            alert('Error submitting DASS report: ' + error.message);
        }
    };

    const handleChange = (e) => {
        setAnswers({
            ...answers,
            [e.target.name]: e.target.value,
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Sample input for Depression questions */}
            <label>
                1. I found it hard to wind down
                <input type="number" name="d1" onChange={handleChange} />
            </label>
            <label>
                2. I was aware of dryness of my mouth
                <input type="number" name="a1" onChange={handleChange} />
            </label>
            {/* Add more inputs for other questions as needed */}

            <button type="submit">Submit Report</button>
        </form>
    );
};

export default DassQ;
