import React, { useEffect, useState } from 'react';
import axios from 'axios';
import config from '../config';

const Predictions = () => {
  const [prediction, setPrediction] = useState(null);
  const [image, setImage] = useState(null);
  const [allPredictions, setAllPredictions] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [retryCount, setRetryCount] = useState(0);

  const fetchCSVDataAndAnalyze = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${config.url}/palpitation_analysis_csv`);
      const { trend_graph, latest_prediction, all_predictions } = res.data;

      setPrediction(latest_prediction);
      setAllPredictions(all_predictions);
      setImage(`data:image/png;base64,${trend_graph}`);
      setError('');
    } catch (err) {
      console.error('Error analyzing CSV data:', err);
      setError('Failed to analyze CSV heart rate data.');
      // Retry up to 3 times
      if (retryCount < 3) {
        setTimeout(() => setRetryCount(retryCount + 1), 2000);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCSVDataAndAnalyze();
  }, [retryCount]);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">Heart Rate Prediction (CSV)</h1>

      {loading ? (
        <p className="text-blue-500">Loading data... Please wait.</p>
      ) : error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <>
          {prediction && <p className="text-lg">Latest Prediction: <strong>{prediction}</strong></p>}

          {image && (
            <div>
              <h2 className="mt-4 font-semibold">Trend Graph</h2>
              <img src={image} alt="Heart Rate Trend" />
            </div>
          )}

          {allPredictions && allPredictions.length > 0 && (
            <div>
              <h2 className="mt-4 font-semibold">All Predictions</h2>
              <ul className="list-disc ml-6">
                {allPredictions.map((pred, index) => (
                  <li key={index}>{pred}</li>
                ))}
              </ul>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Predictions;
