import React, { useState } from 'react';
import './DassQuiz.css'; // Import the CSS file

const DassQuiz = () => {
  // Fetch patient's name from local storage
  const patientName = localStorage.getItem('patientName') || "Anonymous";

  // State to store answers
  const [answers, setAnswers] = useState({
    d1: 0, d2: 0, d3: 0, d4: 0, d5: 0, d6: 0, d7: 0,
    a1: 0, a2: 0, a3: 0, a4: 0, a5: 0, a6: 0, a7: 0,
    s1: 0, s2: 0, s3: 0, s4: 0, s5: 0, s6: 0, s7: 0
  });

  // Handle change in answer selection
  const handleChange = (e) => {
    setAnswers({
      ...answers,
      [e.target.name]: parseInt(e.target.value)
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Normally you would send answers to the backend for storage and processing
    console.log('Submitted answers:', answers);

    // For now, just log them to the console
    alert("Your DASS-21 answers have been submitted!");
  };

  return (
    <div className="dass-quiz">
      <h2>DASS-21 Quiz</h2>
      <p>Name: {patientName}</p>
      <form onSubmit={handleSubmit}>
        <h3>Depression Scale (D)</h3>
        {['d1', 'd2', 'd3', 'd4', 'd5', 'd6', 'd7'].map((item, index) => (
          <div key={index} className="question">
            <label>{index + 1}. I found it hard to wind down</label>
            <input
              type="number"
              name={item}
              value={answers[item]}
              onChange={handleChange}
              min="0"
              max="3"
            />
          </div>
        ))}

        <h3>Anxiety Scale (A)</h3>
        {['a1', 'a2', 'a3', 'a4', 'a5', 'a6', 'a7'].map((item, index) => (
          <div key={index} className="question">
            <label>{index + 1}. I was worried about situations in which I might panic</label>
            <input
              type="number"
              name={item}
              value={answers[item]}
              onChange={handleChange}
              min="0"
              max="3"
            />
          </div>
        ))}

        <h3>Stress Scale (S)</h3>
        {['s1', 's2', 's3', 's4', 's5', 's6', 's7'].map((item, index) => (
          <div key={index} className="question">
            <label>{index + 1}. I found it hard to wind down</label>
            <input
              type="number"
              name={item}
              value={answers[item]}
              onChange={handleChange}
              min="0"
              max="3"
            />
          </div>
        ))}

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default DassQuiz;
