import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from '../config';
import styled from 'styled-components';

export default function DoctorRegistration() {
  const [formData, setFormData] = useState({
    fullname: '',
    specialization: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [doctors, setDoctors] = useState([]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const validatePassword = (password) =>
    /^(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,}$/.test(password);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateEmail(formData.email)) {
      setError('Please enter a valid email address.');
      setMessage('');
      return;
    }

    if (formData.fullname.trim().length === 0) {
      setError('Please enter the doctor\'s full name.');
      setMessage('');
      return;
    }

    if (formData.specialization.trim().length === 0) {
      setError('Please enter the doctor\'s specialization.');
      setMessage('');
      return;
    }

    if (!validatePassword(formData.password)) {
      setError('Password must be at least 8 characters and include a special character.');
      setMessage('');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match.');
      setMessage('');
      return;
    }

    try {
      const response = await axios.post(`${config.url}/registerdoctor`, {
        ...formData
      });

      if (response.status === 200) {
        setMessage('Registration Successful!');
        setError('');
        setFormData({
          fullname: '',
          specialization: '',
          email: '',
          password: '',
          confirmPassword: ''
        });
        fetchDoctors();
      }
    } catch (error) {
      setError(error.response?.data?.message || 'An error occurred.');
      setMessage('');
    }
  };

  const fetchDoctors = async () => {
    try {
      const response = await axios.get(`${config.url}/doctors`);
      setDoctors(response.data);
    } catch (error) {
      console.log('Error fetching doctors', error);
    }
  };

  useEffect(() => {
    fetchDoctors();
  }, []);

  return (
    <Container>
      <Heading>Doctor Registration</Heading>
      {message && <SuccessMessage>{message}</SuccessMessage>}
      {error && <ErrorMessage>{error}</ErrorMessage>}

      <Form onSubmit={handleSubmit}>
        <InputWrapper>
          <Label>Full Name</Label>
          <Input type="text" id="fullname" value={formData.fullname} onChange={handleChange} required />
        </InputWrapper>

        <InputWrapper>
          <Label>Specialization</Label>
          <Input type="text" id="specialization" value={formData.specialization} onChange={handleChange} required />
        </InputWrapper>

        <InputWrapper>
          <Label>Email</Label>
          <Input type="email" id="email" value={formData.email} onChange={handleChange} required />
        </InputWrapper>

        <InputWrapper>
          <Label>Password</Label>
          <Input type="password" id="password" value={formData.password} onChange={handleChange} required />
          <PasswordRules>Must be 8+ characters and include at least 1 special character.</PasswordRules>
        </InputWrapper>

        <InputWrapper>
          <Label>Confirm Password</Label>
          <Input type="password" id="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
        </InputWrapper>

        <SubmitButton type="submit">Register</SubmitButton>
      </Form>

      <DoctorListContainer>
        <DoctorListHeading>List of Registered Doctors</DoctorListHeading>
        <DoctorTable>
          <thead>
            <tr>
              <th>Full Name</th>
              <th>Specialization</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {doctors.map((doctor) => (
              <tr key={doctor._id}>
                <td>{doctor.fullname}</td>
                <td>{doctor.specialization}</td>
                <td>{doctor.email}</td>
              </tr>
            ))}
          </tbody>
        </DoctorTable>
      </DoctorListContainer>
    </Container>
  );
}

// Styled Components

const Container = styled.div`
  background-color: #ffffff;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  width: 400px;
  padding: 20px;
  text-align: center;
  margin: auto;
`;

const Heading = styled.h3`
  font-size: 24px;
  font-weight: bold;
  margin-bottom: 20px;
  color: #2a2a2a;
`;

const SuccessMessage = styled.div`
  margin-top: 15px;
  font-size: 14px;
  color: green;
`;

const ErrorMessage = styled.div`
  margin-top: 15px;
  font-size: 14px;
  color: red;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const InputWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const Label = styled.label`
  font-size: 14px;
  color: #555;
  margin-bottom: 5px;
`;

const Input = styled.input`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
  width: 100%;
`;

const PasswordRules = styled.div`
  font-size: 12px;
  color: #777;
  margin-top: 5px;
`;

const SubmitButton = styled.button`
  padding: 12px;
  background-color: #4caf50;
  color: #fff;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const DoctorListContainer = styled.div`
  margin-top: 30px;
`;

const DoctorListHeading = styled.h4`
  font-size: 18px;
  color: #333;
  margin-bottom: 10px;
`;

const DoctorTable = styled.table`
  width: 100%;
  border-collapse: collapse;

  th, td {
    padding: 10px;
    text-align: left;
    border: 1px solid #ddd;
  }

  th {
    background-color: #f2f2f2;
    font-weight: bold;
  }

  tr:nth-child(even) {
    background-color: #f9f9f9;
  }

  tr:hover {
    background-color: #f1f1f1;
  }
`;
