import React, { useEffect, useState } from 'react';
import axios from 'axios';
import config from '../config';

export default function PalpitationAnalysis() {
  const [risk, setRisk] = useState(null);
  const [heartRate, setHeartRate] = useState(null);
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAnalysis = async () => {
      try {
        const res = await axios.post(`${config.url}/palpitation_analysis`);
        setRisk(res.data.risk_level);
        setHeartRate(res.data.latest_heart_rate);
        setImage(`data:image/png;base64,${res.data.trend_image}`);
      } catch (err) {
        setError('Failed to fetch analysis');
        console.error(err);
      }
    };

    fetchAnalysis();
  }, []);

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2>Palpitation Risk Analysis</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {risk && <h3>Current Risk Level: <span style={{ color: risk === 'High' ? 'red' : risk === 'Moderate' ? 'orange' : 'green' }}>{risk}</span></h3>}
      {heartRate && <p>Latest Heart Rate: <strong>{heartRate} BPM</strong></p>}
      {image && <img src={image} alt="Heart Rate Trend" style={{ maxWidth: '100%', marginTop: '20px' }} />}
    </div>
  );
}
