// src/admin/ViewDoctors.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewDoctors() {
  const [doctors, setDoctors] = useState([]);
  const [newDoctor, setNewDoctor] = useState({
    fullname: '',
    email: '',
    specialization: '',
    password: '',
  });

  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/doctors');
      setDoctors(res.data);
    } catch (err) {
      console.error('Error fetching doctors:', err);
    }
  };

  const handleChange = (e) => {
    setNewDoctor({ ...newDoctor, [e.target.name]: e.target.value });
  };

  const handleAddDoctor = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${config.url}/a`, newDoctor);
      setNewDoctor({ fullname: '', email: '', specialization: '', password: '' });
      fetchDoctors(); // refresh list
    } catch (err) {
      console.error('Error adding doctor:', err);
    }
  };

  return (
    <div>
      <h2>Doctors</h2>
      <ul>
        {doctors.map((doc) => (
          <li key={doc.id}>
            {doc.fullname} - {doc.specialization} ({doc.email})
          </li>
        ))}
      </ul>

      <h3>Add New Doctor</h3>
      <form onSubmit={handleAddDoctor}>
        <input name="fullname" placeholder="Full Name" value={newDoctor.fullname} onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email" value={newDoctor.email} onChange={handleChange} required />
        <input name="specialization" placeholder="Specialization" value={newDoctor.specialization} onChange={handleChange} required />
        <input name="password" type="password" placeholder="Password" value={newDoctor.password} onChange={handleChange} required />
        <button type="submit">Add Doctor</button>
      </form>
    </div>
  );
}
