import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import '../main/login.css';


export default function PatientLogin({ onPatientLogin }) {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${config.url}/checkpatientlogin`, formData);
      if (response.data) {
        onPatientLogin();
        localStorage.setItem('patient', JSON.stringify(response.data));
        navigate("/patienthome");
      } else {
        setMessage("");
        setError("Login Failed. Please check your credentials and try again.");
      }
    } catch (error) {
      setMessage("");
      setError(error.message);
    }
  };

  return (
    <div className="container">
      <h3 className="heading"><u>Patient Login</u></h3>
      {message && <h4 className="message">{message}</h4>}
      {error && <h4 className="error">{error}</h4>}
      <form onSubmit={handleSubmit}>
        <div className="inputContainer">
          <label className="label">Email</label>
          <input className="input" type="email" id="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="inputContainer">
          <label className="label">Password</label>
          <input className="input" type="password" id="password" value={formData.password} onChange={handleChange} required />
        </div>
        <button type="submit" className="button">Login</button>
      </form>
      <p className="registerMessage">Don't have an account? <Link to="/patientregistration">Register here</Link></p>
    </div>
  );
}
