import React, { useState } from "react";
import axios from "axios";

const PatientRegistration = () => {
    const [formData, setFormData] = useState({
        name: "",
        age: "",
        gender: "",
        past_problems: "",
        current_medications: "",
        password: ""
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://127.0.0.1:5000/patient/register", formData);
            alert(response.data.message || "Registration successful!");
        } catch (error) {
            alert(error.response?.data?.error || "Registration failed!");
        }
    };

    return (
        <div>
            <h2>Patient Registration</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
                <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
                <input type="text" name="gender" placeholder="Gender" onChange={handleChange} required />
                <input type="text" name="past_problems" placeholder="Past Problems" onChange={handleChange} required />
                <input type="text" name="current_medications" placeholder="Current Medications" onChange={handleChange} required />
                <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default PatientRegistration;
