import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Predictions = () => {
  const [prediction, setPrediction] = useState(null);
  const [image, setImage] = useState(null);
  const [allPredictions, setAllPredictions] = useState([]);
  const [error, setError] = useState('');

  const fetchThingSpeakDataAndAnalyze = async () => {
    try {
      const thingSpeakRes = await axios.get(
        'https://api.thingspeak.com/channels/2918241/feeds.json?results=20'
      );

      const thingSpeakData = thingSpeakRes.data;
      console.log("Fetched ThingSpeak Data:", thingSpeakData);

      if (!thingSpeakData.feeds || thingSpeakData.feeds.length === 0) {
        throw new Error('No heart rate data in feeds.');
      }

      const backendRes = await axios.post('http://localhost:5000/palpitation_analysis', thingSpeakData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const { trend_graph, latest_prediction, all_predictions } = backendRes.data;

      setPrediction(latest_prediction);
      setAllPredictions(all_predictions);
      setImage(`data:image/png;base64,${trend_graph}`);
      setError('');
    } catch (err) {
      console.error('Error fetching or analyzing data:', err);
      setError('Unable to analyze heart rate data.');
    }
  };

  useEffect(() => {
    fetchThingSpeakDataAndAnalyze();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">Heart Rate Prediction</h1>

      {error && <p className="text-red-500">{error}</p>}

      {prediction && <p className="text-lg">Latest Prediction: <strong>{prediction}</strong></p>}

      {image && (
        <div>
          <h2 className="mt-4 font-semibold">Trend Graph</h2>
          <img src={image} alt="Heart Rate Trend" />
        </div>
      )}

      {allPredictions.length > 0 && (
        <div>
          <h2 className="mt-4 font-semibold">All Predictions</h2>
          <ul className="list-disc ml-6">
            {allPredictions.map((pred, index) => (
              <li key={index}>{pred}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Predictions;
