import React, { useState, useEffect } from 'react';
import axios from 'axios';
import config from '../config';

export default function CurrentHeartRate() {
  const [heartRate, setHeartRate] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHeartRate = async () => {
      try {
        const response = await axios.post(`${config.url}/current_heart_rate`);
        console.log(response.data);  // Log to see the full response

        // Ensure correct data format
        if (response.data && response.data.current_heart_rate !== undefined) {
          setHeartRate(response.data.current_heart_rate);
        } else {
          setError('No heart rate data found.');
        }
      } catch (err) {
        setError('Failed to fetch current heart rate');
        console.error('Error fetching heart rate:', err);
      }
    };

    // Initial fetch
    fetchHeartRate();

    // Set up an interval to refresh data every 5 seconds
    const intervalId = setInterval(fetchHeartRate, 5000);

    // Cleanup function to clear the interval when the component is unmounted
    return () => clearInterval(intervalId);

  }, []); // Empty dependency array ensures the effect runs only once on mount

  return (
    <div>
      {error ? (
        <p>{error}</p>
      ) : heartRate !== null ? (
        <p>Current Heart Rate: {heartRate} BPM</p>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
