import React, { useState } from 'react';

const DassQuiz = () => {
  const [answers, setAnswers] = useState({
    d1: 0, d2: 0, d3: 0, d4: 0, d5: 0, d6: 0, d7: 0,
    a1: 0, a2: 0, a3: 0, a4: 0, a5: 0, a6: 0, a7: 0,
    s1: 0, s2: 0, s3: 0, s4: 0, s5: 0, s6: 0, s7: 0
  });

  const handleChange = (e) => {
    setAnswers({
      ...answers,
      [e.target.name]: parseInt(e.target.value)
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Your DASS-21 answers have been submitted!");
  };

  return (
    <div className="dass-quiz-container">
      <h2 className="heading">DASS-21 Quiz</h2>
      <form onSubmit={handleSubmit}>
        <h3>Depression Scale (D)</h3>
        <div className="input-container">
          <div className="input-group">
            <label>1. I found it hard to wind down</label>
            <input type="number" name="d1" value={answers.d1} onChange={handleChange} min="0" max="3" />
          </div>
          <div className="input-group">
            <label>2. I was aware of dryness of my mouth</label>
            <input type="number" name="d2" value={answers.d2} onChange={handleChange} min="0" max="3" />
          </div>
        </div>

        <div className="input-container">
          <div className="input-group">
            <label>3. I couldnâ€™t seem to experience any positive feeling at all</label>
            <input type="number" name="d3" value={answers.d3} onChange={handleChange} min="0" max="3" />
          </div>
          <div className="input-group">
            <label>4. I experienced breathing difficulty (e.g. excessively rapid breathing)</label>
            <input type="number" name="d4" value={answers.d4} onChange={handleChange} min="0" max="3" />
          </div>
        </div>

        {/* Continue for other sections (Anxiety Scale and Stress Scale) similarly */}

        <button className="submit-button" type="submit">Submit</button>
      </form>
    </div>
  );
};

export default DassQuiz;
