import React, { useState } from 'react';
import axios from 'axios';
import './Registration.css';
import config from '../config';

export default function DoctorRegistration() {
  const [formData, setFormData] = useState({
    fullname: '',
    specialization: '',
    email: '',
    password: '',
    confirmPassword: ''
  });

  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const validatePassword = (password) =>
    /^(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,}$/.test(password);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateEmail(formData.email)) {
      setError('Please enter a valid email address.');
      setMessage('');
      return;
    }

    if (formData.fullname.trim().length === 0) {
      setError('Please enter the doctor\'s full name.');
      setMessage('');
      return;
    }

    if (formData.specialization.trim().length === 0) {
      setError('Please enter the doctor\'s specialization.');
      setMessage('');
      return;
    }

    if (!validatePassword(formData.password)) {
      setError('Password must be at least 8 characters and include a special character.');
      setMessage('');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match.');
      setMessage('');
      return;
    }

    try {
      const response = await axios.post(`${config.url}/registerdoctor`, {
        ...formData
      });

      if (response.status === 200) {
        setMessage('Registration Successful!');
        setError('');
        setFormData({
          fullname: '',
          specialization: '',
          email: '',
          password: '',
          confirmPassword: ''
        });
      }
    } catch (error) {
      setError(error.response?.data?.message || 'An error occurred.');
      setMessage('');
    }
  };

  return (
    <div className="registration-container">
      <h3 className="heading">Doctor Registration</h3>
      {message ? (
        <h4 align="center" className="success-message">{message}</h4>
      ) : (
        <h4 align="center" className="error-message">{error}</h4>
      )}

      <form onSubmit={handleSubmit} className="registration-form">
        <div>
          <label>Full Name</label>
          <input type="text" id="fullname" value={formData.fullname} onChange={handleChange} required />
        </div>

        <div>
          <label>Specialization</label>
          <input type="text" id="specialization" value={formData.specialization} onChange={handleChange} required />
        </div>

        <div>
          <label>Email</label>
          <input type="email" id="email" value={formData.email} onChange={handleChange} required />
        </div>

        <div>
          <label>Password</label>
          <input type="password" id="password" value={formData.password} onChange={handleChange} required />
          <div className="password-rules">
            Must be 8+ characters and include at least 1 special character.
          </div>
        </div>

        <div>
          <label>Confirm Password</label>
          <input type="password" id="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
        </div>

        <button type="submit">Register</button>
      </form>
    </div>
  );
}
