import React, { useState } from "react";

export default function PatientRegistration() {
  const [patient, setPatient] = useState({
    name: "",
    age: "",
    gender: "",
    past_problems: "",
    current_medications: "",
    password: "",
  });

  const handleChange = (e) => {
    setPatient({ ...patient, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://127.0.0.1:5000/patient/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(patient),
      });

      const data = await response.json();
      alert(data.message || "Patient registered successfully!");
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to register patient.");
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto bg-white shadow-md rounded-lg">
      <h2 className="text-xl font-bold mb-4">Patient Registration</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={patient.name} onChange={handleChange} required className="w-full p-2 border rounded mb-2" />
        <input type="number" name="age" placeholder="Age" value={patient.age} onChange={handleChange} required className="w-full p-2 border rounded mb-2" />
        <select name="gender" value={patient.gender} onChange={handleChange} required className="w-full p-2 border rounded mb-2">
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Other">Other</option>
        </select>
        <input type="text" name="past_problems" placeholder="Past Problems" value={patient.past_problems} onChange={handleChange} className="w-full p-2 border rounded mb-2" />
        <input type="text" name="current_medications" placeholder="Current Medications" value={patient.current_medications} onChange={handleChange} className="w-full p-2 border rounded mb-2" />
        <input type="password" name="password" placeholder="Password" value={patient.password} onChange={handleChange} required className="w-full p-2 border rounded mb-2" />
        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded">Register</button>
      </form>
    </div>
  );
}
