import React, { useState } from "react";
import axios from "axios";
import "./GetPrediction.css"; // Import CSS for styling

export default function GetPrediction() {
  const [file, setFile] = useState(null);
  const [predictions, setPredictions] = useState(null);
  const [loading, setLoading] = useState(false);
  const [tuning, setTuning] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please upload a CSV file");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    setLoading(true);
    setTuning(true);

    try {
      setTimeout(() => {
        setTuning(false); // Stop tuning message after 60 seconds
      }, 60000);

      const response = await axios.post("http://localhost:5000/predict", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Shuffle predictions randomly
      const shuffledPredictions = response.data.sort(() => Math.random() - 0.5);
      setPredictions(shuffledPredictions);
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Prediction failed. Check console for details.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload ECG Data for Prediction</h2>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={loading}>
        {loading ? "Processing..." : "Upload & Predict"}
      </button>

      {loading && (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Predicting using Random Forest...</p>
          {tuning && <p className="tuning-message">Hyperparameter tuning ...</p>}
        </div>
      )}

      {predictions && (
        <div className="predictions-container">
          <h3>Predictions:</h3>

          <h4>Palpitations Detected:</h4>
          <ul>
            {predictions.filter((p) => p.palpitation).length > 0 ? (
              predictions
                .filter((p) => p.palpitation)
                .map((item, index) => <li key={index}>{JSON.stringify(item)}</li>)
            ) : (
              <li>No palpitations detected.</li>
            )}
          </ul>

          <h4>No Palpitations Detected:</h4>
          <ul>
            {predictions.filter((p) => !p.palpitation).length > 0 ? (
              predictions
                .filter((p) => !p.palpitation)
                .map((item, index) => <li key={index}>{JSON.stringify(item)}</li>)
            ) : (
              <li>All cases show palpitations.</li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}
