import React, { useEffect, useState } from 'react';
import axios from 'axios';
import config from '../config';

const Predictions = () => {
  const [prediction, setPrediction] = useState(null);
  const [image, setImage] = useState(null);
  const [heartRateSpread, setHeartRateSpread] = useState(null);
  const [allPredictions, setAllPredictions] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [retryCount, setRetryCount] = useState(0);

  // Function to fetch heart rate spread data
  const fetchHeartRateSpread = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${config.url}/heart_rate_spread`);
      const { spread_graph } = res.data;
      setHeartRateSpread(`data:image/png;base64,${spread_graph}`);
      setError('');
    } catch (err) {
      console.error('Error fetching heart rate spread:', err);
      setError('Failed to fetch heart rate spread graph.');
    } finally {
      setLoading(false);
    }
  };

  // Function to fetch the palpitation analysis
  const fetchPalpitationAnalysis = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${config.url}/palpitation_analysis_csv`);
      const {
        trend_graph,
        latest_prediction,
        all_predictions,
        accuracy,
        classification_report,
        confusion_matrix,
      } = res.data;

      setPrediction(latest_prediction);
      setAllPredictions(all_predictions);
      setImage(`data:image/png;base64,${trend_graph}`);
      setError('');
    } catch (err) {
      console.error('Error analyzing CSV data:', err);
      setError('Failed to analyze CSV heart rate data.');
      if (retryCount < 3) {
        setTimeout(() => setRetryCount(retryCount + 1), 2500);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Fetch heart rate spread immediately
    fetchHeartRateSpread();

    // Timeout to fetch palpitation analysis after 30 seconds
    const timer = setTimeout(() => {
      fetchPalpitationAnalysis();
    }, 30000); // 30 seconds delay before fetching data

    // Clean up the timer
    return () => clearTimeout(timer);
  }, [retryCount]);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">Heart Rate Prediction (CSV)</h1>

      {loading ? (
        <p className="text-blue-500">Loading data... Please wait.</p>
      ) : error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <>
          {/* Display heart rate spread graph initially */}
          {heartRateSpread && (
            <div>
              <h2 className="mt-4 font-semibold">Heart Rate Spread</h2>
              <img src={heartRateSpread} alt="Heart Rate Spread" />
            </div>
          )}

          {/* Display latest prediction and trend graph after 30 seconds */}
          {prediction && <p className="text-lg mt-4">Latest Prediction: <strong>{prediction}</strong></p>}

          {image && (
            <div>
              <h2 className="mt-4 font-semibold">Trend Graph</h2>
              <img src={image} alt="Heart Rate Trend" />
            </div>
          )}

          {allPredictions && allPredictions.length > 0 && (
            <div>
              <h2 className="mt-4 font-semibold">All Predictions</h2>
              <ul className="list-disc ml-6">
                {allPredictions.map((pred, index) => (
                  <li key={index}>{pred}</li>
                ))}
              </ul>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Predictions;
