import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function CurrentHeartRate() {
  const [heartRate, setHeartRate] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch the current heart rate data from the backend
    const fetchHeartRate = async () => {
      try {
        const response = await axios.get('http://localhost:5000/current_heart_rate'); // Adjust the URL as needed
        setHeartRate(response.data.current_heart_rate);
      } catch (err) {
        setError('Failed to fetch current heart rate');
      }
    };

    fetchHeartRate();
  }, []);

  return (
    <div>
      <h1>Current Heart Rate</h1>
      {error && <p>{error}</p>}
      {heartRate ? (
        <p>The current heart rate is: {heartRate} BPM</p>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
