import React, { useState } from 'react';
import './DassQuiz.css'; // Import the CSS file

const DassQuiz = () => {
  const [answers, setAnswers] = useState({
    d1: 0, d2: 0, d3: 0, d4: 0, d5: 0, d6: 0, d7: 0,
    a1: 0, a2: 0, a3: 0, a4: 0, a5: 0, a6: 0, a7: 0,
    s1: 0, s2: 0, s3: 0, s4: 0, s5: 0, s6: 0, s7: 0
  });

  const handleChange = (e) => {
    setAnswers({
      ...answers,
      [e.target.name]: parseInt(e.target.value)
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted answers:', answers);
    alert("Your DASS-21 answers have been submitted!");
  };

  return (
    <div className="container">
      <h2 className="heading">DASS-21 Quiz</h2>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="column">
            <h3>Depression Scale (D)</h3>
            {['d1', 'd2', 'd3', 'd4'].map((item, index) => (
              <div className="inputContainer" key={index}>
                <label className="label">Q{index + 1}: I found it hard to wind down</label>
                <input
                  type="number"
                  name={item}
                  value={answers[item]}
                  onChange={handleChange}
                  min="0"
                  max="3"
                  className="input"
                />
              </div>
            ))}
          </div>
          <div className="column">
            <h3>Anxiety Scale (A)</h3>
            {['a1', 'a2', 'a3', 'a4'].map((item, index) => (
              <div className="inputContainer" key={index}>
                <label className="label">Q{index + 1}: I was worried about situations in which I might panic</label>
                <input
                  type="number"
                  name={item}
                  value={answers[item]}
                  onChange={handleChange}
                  min="0"
                  max="3"
                  className="input"
                />
              </div>
            ))}
          </div>
        </div>

        <div className="row">
          <div className="column">
            <h3>Stress Scale (S)</h3>
            {['s1', 's2', 's3', 's4'].map((item, index) => (
              <div className="inputContainer" key={index}>
                <label className="label">Q{index + 1}: I found it hard to wind down</label>
                <input
                  type="number"
                  name={item}
                  value={answers[item]}
                  onChange={handleChange}
                  min="0"
                  max="3"
                  className="input"
                />
              </div>
            ))}
          </div>
          <div className="column">
            <h3>Final Section</h3>
            {/* Add any additional content or inputs as needed */}
          </div>
        </div>

        <button type="submit" className="button">Submit</button>
      </form>
    </div>
  );
};

export default DassQuiz;
